// Cost Anomaly Detection
// Compares last 24 hours to 7-day average and flags anomalies

const athena = new AWS.Athena({ region: 'ap-south-1' });
const threshold = 1.5; // 50% above average triggers alert

console.log('ðŸ” Cost Anomaly Detection');
console.log('===========================\n');
console.log(`Threshold: ${threshold}x average\n`);

// Get daily costs for last 7 days
const query = `
SELECT 
  DATE(\"lineitem/usagestartdate\") as day,
  \"product/productname\" as service,
  ROUND(SUM(\"lineitem/unblendedcost\"), 2) as daily_cost
FROM \"raw-data-v1\".\"raw_aws_amnic\"
WHERE \"lineitem/usagestartdate\" >= CURRENT_DATE - INTERVAL '7' DAY
  AND \"lineitem/unblendedcost\" > 0
GROUP BY DATE(\"lineitem/usagestartdate\"), \"product/productname\"
ORDER BY day DESC, daily_cost DESC
`;

